template.script:
$(function(){
    $('button.btn-danger').click(function(){
        var promotion_id = $(this).parent().parent().find('input.form-control').attr('id');;
        $.ajax({
            async : false,
            type: "POST",
            url: "/shop/user/info/data_new",
            data: "promotion_id="+promotion_id
            }).done(function(msg){
                if (msg){
                    if (msg == '0'){
                        alert('获取优惠失败，请联系管理员！');
                    }
                    if (msg == '1'){
                        alert('该优惠您已经报名！');
                    }
                    if (msg == '2'){
                        var r=confirm("报名成功!");
                        $("#conform_promotion").submit();
                    }
                }
                else{
                    alert('报名失败，请稍后重试或联系管理员！')
                }
            }).fail(function(xmlHttpRequest,statusText,errorThrown) {
        });
    });
});
